import { Component, OnDestroy, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { UsersService } from '../../users.service';
import { Subscription } from 'rxjs';
import { User } from 'src/app/core/utilities/types/core-types';
import {MatIconModule} from '@angular/material/icon';
import {MatDividerModule} from '@angular/material/divider';
import {MatButtonModule} from '@angular/material/button';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-user-list',
  standalone: true,
  imports: [CommonModule, RouterModule, MatIconModule, MatDividerModule, MatButtonModule],
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.scss']
})
export class UserListComponent implements OnInit, OnDestroy{

  userService = inject(UsersService);
  usersSubscription!:Subscription;
  users:User[] = [];
constructor(){
  console.log('USERS CONSTRUCTOR');
}

ngOnInit(): void {
  this.usersSubscription = this.userService.getUsers().subscribe({
    next: res =>{
      console.log('response',res);
      this.users = res;
    },
    error: err => {
       console.error(err.message);
    },

  });
}

ngOnDestroy(): void {
  this.usersSubscription?.unsubscribe();
}
}
