  //       console.log(res);
  //       this.user = {...res};
  //       this.userSub?.unsubscribe();
  //     },
  //     error:(err) => console.error(err)
  // });
}

  ngOnDestroy(): void {
    this.routeSub?.unsubscribe();
  }
}
